export default {
  name: 'Admin',
  data() {
    return {
      profile: {
        name: '',
        title: '',
        avatar: '',
        about: '',
        skills: [],
        projects: [],
        social: {
          github: '',
          email: '',
          linkedin: ''
        },
        theme: {
          primaryColor: '#3498db',
          textColor: '#2c3e50',
          backgroundImage: '',
          fontFamily: "'Helvetica Neue', Arial",
          isDark: false
        }
      },
      currentLang: 'zh',
      newSkill: '',
      showProjectModal: false,
      editingProject: null,
      currentProject: {
        id: null,
        title: '',
        description: '',
        link: ''
      }
    };
  },
  computed: {
    bgPreviewStyle() {
      return {
        backgroundImage: this.profile.theme.backgroundImage ? `url(${this.profile.theme.backgroundImage})` : 'none'
      };
    }
  },
  methods: {
    async loadProfile() {
      try {
        const response = await fetch(`http://localhost:3000/api/profile?lang=${this.currentLang}`);
        const data = await response.json();
        this.profile = data;
      } catch (error) {
        console.error('Error loading profile:', error);
      }
    },
    async handleFileChange(event) {
      const file = event.target.files[0];
      if (!file) return;
      const formData = new FormData();
      formData.append('avatar', file);
      try {
        const response = await fetch('http://localhost:3000/api/upload', {
          method: 'POST',
          body: formData
        });
        const data = await response.json();
        if (data.url) {
          this.profile.avatar = data.url;
          await this.saveBasicInfo();
        }
      } catch (error) {
        console.error('Upload error:', error);
        alert('上传失败，请重试');
      }
    },
    async saveBasicInfo() {
      try {
        const response = await fetch(`http://localhost:3000/api/profile?lang=${this.currentLang}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(this.profile)
        });
        const data = await response.json();
        if (data.success) {
          alert('保存成功');
          await this.loadProfile();
        }
      } catch (error) {
        console.error('Error saving profile:', error);
        alert('保存失败');
      }
    },
    switchLang(lang) {
      this.currentLang = lang;
      this.loadProfile();
    },
    async handleBgChange(event) {
      const file = event.target.files[0];
      if (!file) return;
      const formData = new FormData();
      formData.append('background', file);
      try {
        const response = await fetch('http://localhost:3000/api/upload-bg', {
          method: 'POST',
          body: formData
        });
        const data = await response.json();
        if (data.url) {
          this.profile.theme.backgroundImage = data.url;
          await this.saveTheme();
        }
      } catch (error) {
        console.error('Upload error:', error);
        alert(this.currentLang === 'zh' ? '上传失败，请重试' : 'Upload failed');
      }
    },
    removeBg() {
      this.profile.theme.backgroundImage = '';
      this.saveTheme();
    },
    async saveTheme() {
      try {
        const response = await fetch(`http://localhost:3000/api/profile?lang=${this.currentLang}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(this.profile)
        });
        const data = await response.json();
        if (data.success) {
          alert(this.currentLang === 'zh' ? '主题保存成功' : 'Theme saved');
          await this.loadProfile();
        }
      } catch (error) {
        console.error('Error saving theme:', error);
        alert(this.currentLang === 'zh' ? '保存失败' : 'Save failed');
      }
    },
    getRGB(hex) {
      const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
      return result ? {
        r: parseInt(result[1], 16),
        g: parseInt(result[2], 16),
        b: parseInt(result[3], 16)
      } : {
        r: 0,
        g: 0,
        b: 0
      };
    },
    setThemeMode(isDark) {
      this.profile.theme.isDark = isDark;
      if (isDark) {
        this.profile.theme.textColor = '#ffffff';
        document.body.classList.add('dark-mode');
      } else {
        this.profile.theme.textColor = '#2c3e50';
        document.body.classList.remove('dark-mode');
      }
      this.saveTheme();
    },
    async toggleTheme() {
      this.profile.theme.isDark = !this.profile.theme.isDark;
      // 更新服务器数据
      try {
        await fetch(`http://localhost:3000/api/profile?lang=${this.currentLang}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(this.profile)
        });
      } catch (error) {
        console.error('Error saving theme:', error);
      }
    },
    addSkill() {
      if (this.newSkill.trim()) {
        if (!this.profile.skills) {
          this.profile.skills = [];
        }
        this.profile.skills.push(this.newSkill.trim());
        this.newSkill = '';
        this.saveBasicInfo();
      }
    },
    removeSkill(index) {
      this.profile.skills.splice(index, 1);
      this.saveBasicInfo();
    },
    editProject(project) {
      this.editingProject = project;
      this.currentProject = {
        ...project
      };
      this.showProjectModal = true;
    },
    async deleteProject(projectId) {
      if (confirm(this.currentLang === 'zh' ? '确定要删除这个项目吗？' : 'Are you sure to delete this project?')) {
        this.profile.projects = this.profile.projects.filter(p => p.id !== projectId);
        await this.saveBasicInfo();
      }
    },
    async saveProject() {
      if (this.editingProject) {
        // 更新现有项目
        const index = this.profile.projects.findIndex(p => p.id === this.editingProject.id);
        if (index !== -1) {
          this.profile.projects[index] = {
            ...this.currentProject
          };
        }
      } else {
        // 添加新项目
        const newProject = {
          ...this.currentProject,
          id: Date.now() // 简单的ID生成
        };
        if (!this.profile.projects) {
          this.profile.projects = [];
        }
        this.profile.projects.push(newProject);
      }
      await this.saveBasicInfo();
      this.closeProjectModal();
    },
    closeProjectModal() {
      this.showProjectModal = false;
      this.editingProject = null;
      this.currentProject = {
        id: null,
        title: '',
        description: '',
        link: ''
      };
    },
    async saveSocialLinks() {
      try {
        await this.saveBasicInfo();
        alert(this.currentLang === 'zh' ? '保存成功' : 'Saved successfully');
      } catch (error) {
        console.error('Error saving social links:', error);
        alert(this.currentLang === 'zh' ? '保存失败' : 'Save failed');
      }
    },
    handleLogout() {
      if (confirm(this.currentLang === 'zh' ? '确定要退出吗？' : 'Are you sure to logout?')) {
        localStorage.removeItem('token');
        this.$router.push('/login');
      }
    }
  },
  async created() {
    const urlParams = new URLSearchParams(window.location.search);
    this.currentLang = urlParams.get('lang') || localStorage.getItem('lang') || 'zh';
    await this.loadProfile();
    // 初始化时设置主题
    if (this.profile.theme?.isDark) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }
};
